rss.script:
translate_base_url  = "http://127.0.0.1/cgi-bin/translate?";
cgiconf = readStringFromFile("/usr/local/etc/translate/etc/cgi.conf");
if(cgiconf != null)
{
  value = getStringArrayAt(cgiconf, 0);
  if(value != null && value != "")
  {
    translate_base_url = value;
    print("cgi.conf=",value);
  }
}

rss.onRefresh:
setRefreshTime(-1);
itemCount = getPageInfo("itemCount");
middleItem = Integer(itemCount / 2);
if(startitem == "middle")
  setFocusItemIndex(middleItem);
else
if(startitem == "right")
  setFocusItemIndex(middleItem);
redrawDisplay();

image.script:
idx = getQueryItemIndex();
focus = getFocusItemIndex();
if(focus==idx) 
{
  annotation = getItemInfo(idx, "annotation");
}
getItemInfo(idx, "image");

offsetXPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 50 * (1 - columnCount/3); else 12 + 37 * (1 - columnCount/3);

offsetYPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 0; else 6;

widthPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 100 * columnCount/3; else 75 * columnCount/3;

heightPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 50; else 37;

fontSize.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "22"; else "18";

foregroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "255:255:255"; else "75:75:75";

onUserInput.script:
ret = "false";
userInput = currentUserInput();
majorContext = getPageInfo("majorContext");

print("*** majorContext=",majorContext);
print("*** userInput=",userInput);

ret;
